import classNames from "classnames";
import * as FileSaver from "file-saver";
import XLSX from "sheetjs-style";

function EditorTopMenu({pageTitle, menuOptions =[], selected, onSave, onRun, onHelp, onReset, downloadType = "image", tableData = [], fileName="export"}) {

  const exportToExcel = async (fileName, excelData=[]) => {
    try {
      if(excelData.length>0){
        const fileType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8"
        const fileExtension = ".xlsx";

        const ws = XLSX.utils.json_to_sheet(excelData);
        const wb = {Sheets: {'data': ws}, SheetNames: ['data']};
        const excelBuffer = XLSX.write(wb, {bookType: "xlsx", type: "array"});
        const data = new Blob([excelBuffer], {type: fileType});
        FileSaver.saveAs(data, fileName+fileExtension)
      }else{
        alert("No available data")
      }
    } catch (error) {
      alert(error)
    }
  }

  const onDownload = () => {
    if(downloadType === "image"){
      window.print()
    }else{
      exportToExcel(fileName, tableData)
    }
  }

  const renderedDropdownOptions = menuOptions.map(option => {
    const thisClassName = classNames("button is-white dropdown-item ", selected === option.title && "is-active")
    return (
      <button className={thisClassName} onClick={option.onClick} key={option.title}>
        {option.title}
      </button>
    )
  });

  return(
    <>
      <div className="is-flex is-justify-content-space-between is-align-items-center">
          <div className="is-size-4 has-text-weight-semibold">
          {pageTitle && <span className="mr-4">{`${pageTitle}`}</span>}
          {menuOptions.length>0 && (
            <div className="dropdown is-hoverable">
              <div className="dropdown-trigger">
                <button className="button" aria-haspopup="true" aria-controls="dropdown-menu4">
                  <span>{selected}</span>
                  <span className="icon is-small">
                    <i className="fas fa-angle-down" aria-hidden="true"></i>
                  </span>
                </button>
              </div>
              <div className="dropdown-menu" id="dropdown-menu4" role="menu">
              <div className="dropdown-content">
                {renderedDropdownOptions}
              </div>
              </div>
            </div>
          )}
          </div>
          <div className="buttons">
            {onHelp && (<button className="button is-white" onClick={onHelp}>Help</button>)}
            {onSave && (<button className="button" onClick={onSave}>Save</button>)}
            {onRun &&  (<button className="button" onClick={onRun}>Run</button>)}
            {onReset && (<button className="button" onClick={onReset}>Reset</button>)}
            <button className="button mr-2" onClick={onDownload}>Download</button>
          </div>
      </div>
      <div className="block"/>
    </>
  );
}

