import classNames from "classnames";

import TableCheckbox from "./TableCheckbox";
import TableDeleteButton from "./TableDeleteRowButton";
import TableDropdown from "./TableDropdown";
import TableInputField from "./TableInputField";
import TableSearchInput from "./TableSearchInput";

function TableRow({rowCols, onColEdit, onColSearch, onRowDelete, rowId, rowHeader, rowIsNew}) {

  const renderedCols = rowCols.map((col) => {

    const classes = classNames("is-vcentered", {"has-text-centered": col.colType === "display"});

    const onColChange = (newVal) => {
      onColEdit(rowId, col.colId, newVal)
    }

    const onColSearch = (newTerm) => {
      onColSearch(rowId, col.colId, newTerm)
    }

    return(
      <td key={col.colId} className={classes}>
        {col.colType === "display"&&(
          <>{col.colValue}</>
        )}
        {col.colType.includes("input")&&(
          <TableInputField value={col.colValue} onChange={onColChange} type={col.colType} limit={col.colLimit? col.colLimit:null}/>
        )}
        {col.colType === "check"&&(
          <TableCheckbox value={col.colValue} onChange={onColChange}/>
        )}
        {col.colType === "search"&&(
          <TableSearchInput 
            value={col.colValue} 
            onChange={onColChange} 
            onSearch={onColSearch} 
            options={col.colMenuItems} 
            isLoading={col.colIsLoading}            
          />
        )}
        {col.colType === "drop"&&(
          <TableDropdown 
            value={col.colValue} 
            onChange={onColChange} 
            options={col.colMenuItems} 
            isLoading={col.colIsLoading}
          />
        )}
      </td>
    );
  });


  return(
    <>
      <th key={"head"+rowId} className="is-vcentered">
        {rowHeader}
      </th>

      {renderedCols}

      {rowIsNew && (
        <td key={"del"+rowId} className="is-vcentered">
          <TableDeleteButton onClick={()=>onRowDelete(rowId)}/>
        </td>
      )}
    </>
  )
}

TableRow.defaultProps = {
  rowId: 0,
  rowHeader: "",
  rowCols: [],
  onColEdit: () => {},
  onColSearch: () => {},
  onRowDelete: () => {},
  rowIsNew: false
}
export default TableRow;
