import classNames from "classnames";
import { useState } from "react";

function TableSearchInput({value, onChange, onSearch, options, isLoading}) {
  const [thisValue, setThisValue] = useState(value);
  const [isActive, setIsActive] = useState(options.length>0);

  const wrapperClass = classNames('dropdown', {"is-active": isActive})
  const formClass = classNames('control has-icons-left', {"is-loading": isLoading})

  const openDropdown = () => setIsActive(true)
  const closeDropdown = () => setIsActive(false)

  const onInputChange = (event) => {
    setThisValue(event.target.value)
  }

  const handleFormSubmit = (event) => {
    //this prevents page from reloading after user hits enter in any
    //of the inputs inside the form
    event.preventDefault();
    //this allows the following lines to trigger on submission
    openDropdown()
    onSearch(thisValue)
  }

  const renderDropdownContent = options.map((item, idx) => {
    const onOptionSelect = () => {
      onChange(item)
      closeDropdown()
    }
    // eslint-disable-next-line
    return <a className="dropdown-item" key={idx} onClick={onOptionSelect}>{item}</a>
  })

  return(
    <div className={wrapperClass}>
      <div className="dropdown-trigger">
        <form className={formClass} onSubmit={handleFormSubmit}>
          <input className="input " type="text" value={thisValue} onChange={onInputChange} onBlur={closeDropdown} />
          <span className="icon is-left">
            <i className="fas fa-search" aria-hidden="true"></i>
          </span>
        </form>
      </div>
      <div className="dropdown-menu" id="dropdown-menu3" role="menu">
        <div className="dropdown-content">
          {renderDropdownContent}
        </div>
      </div>
    </div>
  );
}

TableSearchInput.defaultProps = {
  value: null,
  onSearch: () => {},
  options: [],
  onChange: () => {},
  isLoading: false,
}

export default TableSearchInput;
