import classNames from "classnames";

function TableDropdown({value, options, onChange, isLoading}) {

  const classes = classNames("select", {"is-loading": isLoading})

  const renderDropdownContent = options.map((item, idx) => {

    const onThisSelect = () => onChange(item)

    return <option key={idx} onClick={onThisSelect}>{item}</option>
  })

  return(
    <div className={classes}>
      <select defaultValue={value} disabled={isLoading}>
        {renderDropdownContent}
      </select>
    </div>
  );
}

TableDropdown.defaultProps = {
  value: null,
  options: [],
  onChange: () => {},
  isLoading: false,
}

export default TableDropdown;
