import { useState } from "react";

function TableInputField({value, onChange, type="", limit}) {
  const [thisValue, setThisValue] = useState(value)
  const thisInputType = type.includes(",")? type.split(",")[1]:"text"

  const lowerLimit = limit? limit[0] :null
  const upperLimit = limit? limit[1] : null

  const onInputChange = (event) => {
    let newValue = event.target.value;

    if(upperLimit != null){
      if(newValue>upperLimit){
        newValue = upperLimit
      }
    }

    if(lowerLimit != null){
      if(newValue<lowerLimit){
        newValue = lowerLimit
      }
    }

   setThisValue(newValue)
  }

  const handleFormSubmit = (event) => {
    //this prevents page from reloading after user hits enter in any
    //of the inputs inside the form
    event.preventDefault();
    //this allows the following lines to trigger on submission
    if(thisValue === "" && thisInputType === "number"){
      onChange(lowerLimit? lowerLimit:0)
    }else{
      onChange(thisValue )
    }
  }

  return (
    <form onSubmit={handleFormSubmit}>
      <input className="input" type={thisInputType} value={thisValue} onChange={onInputChange} onBlur={handleFormSubmit}/>
    </form>
  )
}

TableInputField.defaultProps = {
  value: null,
  onChange: () => {}
}

export default TableInputField;
