import { AiOutlineFolder, AiOutlineFolderOpen } from "react-icons/ai";

function CollapsibleMenu({children, title, isTitle = true, state, onToggle}) {
  return(
    <>
      <button className="button is-white" onClick={onToggle}>
        <span>
          <span className="icon">
            {state? (<AiOutlineFolderOpen/>):(<AiOutlineFolder/>)}
          </span>
          <span><p className={`icon-text ${isTitle? "is-uppercase is-size-7":"has-text-weight-semibold"}`}>{title}</p></span>
        </span> 
      </button>   

      {state && (
        <div className="ml-2">
          {children}
        </div>
      )}
      <div className="my-2"/>
    </>
  );
}

export default CollapsibleMenu;
