import "./02_1HomePage.css";

import axios from "axios";
import { httpPaths, allowServerComm } from "../utils/appConstants";
import { v4 as uuidv4 } from 'uuid';

import { useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";

import { VscFileSubmodule } from "react-icons/vsc";
import { AiOutlineLineChart } from "react-icons/ai";
import { useDispatch, useSelector } from "react-redux";

import ProjectCard from "../components/ProjectCard";

import useAuth from "../hooks/use-auth";
import { formatDigit, getNavURL, processServerResponse } from "../utils/appUtils";

import { restore_projects, create_project, delete_project_by_id, update_project_by_id } from "../redux/features/projectsSlice";
import { update_allowed_tabs, update_selected_project_id } from "../redux/features/allowedTabsSlice";
import { update_bi_inlet_object, update_bi_outlet_object } from "../redux/features/basicInfoSlice";
import { BIInOuletSample } from "../utils/testData";
import { cloneDeep } from "lodash";

function HomePage() {
  const { userToken } = useAuth();
  // eslint-disable-next-line
  const navigate = useNavigate();
  const reduxDispatch = useDispatch();

  const [pageState, setPageState] = useState(0) //0: home, 1:new project, 2: start
  const [projectTitle, setProjectTitle] = useState("")
  const [projectDescription, setProjectDescription] = useState("")
  const [pathFileList, setPathFileList] = useState([])
  const [selectedFile, setSelectedFile] = useState(null)

  const [showLoadingScreen, setShowLoadingScreen] = useState(false)

  const Projects = useSelector(state => state.projects);

  const restoreProjects = useCallback((list) => reduxDispatch(restore_projects(list)), [reduxDispatch]);
  const createProject = useCallback((newProject) => reduxDispatch(create_project(newProject)), [reduxDispatch]);
  const deleteProjectById = useCallback((id) => reduxDispatch(delete_project_by_id(id)), [reduxDispatch]);
  const updateProjectById = useCallback((id) => reduxDispatch(update_project_by_id(id)), [reduxDispatch]);

  const handleProjectTitleInput = (event) => {
    setProjectTitle(event.target.value)
  }

  const handleProjectDescriptionInput = (event) => {
    setProjectDescription(event.target.value)
  }

  const handleProjectFileSubmit = (event) => {
    event.preventDefault();

    const files = event.target.files;

    let finalFileList = []

    for (const file of files) {
      let fileName = file.webkitRelativePath
      if(!fileName.includes(".DS_Store")){
        if(fileName.includes(".bkp")){
          finalFileList.push(fileName)
        }
      }
    }
    setSelectedFile(finalFileList[0])
    setPathFileList(finalFileList)
  }

  const onBackPress = () => {
    if(pageState ===1){
      setPathFileList([])
      setSelectedFile(null)
      setProjectTitle("")
      setProjectDescription("")
    }
    setPageState(0)
  }

  const extractFileName = (name="") => {
    if(name !== ""){
      let nameArr = name.split("/")

      if(nameArr.length>0){
        return nameArr[nameArr.length-1]
      }

      return name
    }
    return name
  }

  const handleOptionSelect = (e) => {
    if(typeof e.target.value === "string"){
      // console.log(e.target.value);
      setSelectedFile(e.target.value)
    }
  }

  const handleNewProjectSave = (e) => {
    e.preventDefault();

    if(projectTitle === ""){
      alert("Title is required")
      return;
    }

    if(pathFileList.length === 0){
      alert("File missing")
      return;
    }

    let newMsg = {
      project_name: projectTitle,
      path: selectedFile,
      file_name: extractFileName(selectedFile),
      project_description: projectDescription,
      email: userToken?.email
    }

    if(allowServerComm){
      setShowLoadingScreen(true)

      axios.post(httpPaths["createProject"], newMsg)
      .then((response)=>{
        let isOk = processServerResponse(response)

        if(!isOk){
          alert("Something went wrong")
          setShowLoadingScreen(false)
        }else{
          createProject(isOk)
          alert("Successfully created project!")
          setPageState(0)
          setShowLoadingScreen(false)
        }

      }).catch(e => {
        alert("Error on save new project: "+e)
        setShowLoadingScreen(false)
      })
    }else{
      let msgCopy = cloneDeep(newMsg)
      delete newMsg.email;
      delete newMsg.path;
      delete newMsg.file_name;

      let today = new Date();

      createProject({
        ...msgCopy,
        project_id: uuidv4(),
        createtime: `${today.getFullYear()}.${formatDigit(today.getMonth()+1)}.${formatDigit(today.getDate())}`
      })

      alert("Successfully created project!")
      setPageState(0)
      setShowLoadingScreen(false)
    }
  }

  const handleProjectEdit = (obj) => {
    //TODO: update with Sherpa API
    setShowLoadingScreen(true)
    let newMsg = {
      project_name: obj.title,
      project_description: obj.description,
    }

    if(allowServerComm){
      axios.put(`${httpPaths["editProject"]}/${obj.id}`, newMsg)
      .then(()=>{
        updateProjectById({
          project_id: obj.id,
          ...newMsg
        })
        setShowLoadingScreen(false)
      }).catch(e => {
        alert("Error on project edit: "+e)
        setShowLoadingScreen(false)
      })
    }else{
      updateProjectById({
        project_id: obj.id,
        ...newMsg
      })
      setShowLoadingScreen(false)
    }
  }

  const handleProjectDelete = (id) => {
    //TODO: update with Sherpa API
    if(allowServerComm){
      setShowLoadingScreen(true)
      axios.delete(`${httpPaths["deleteProject"]}/${id}`)
      .then(()=>{
        deleteProjectById({project_id: id})
        setShowLoadingScreen(false)
      }).catch(e => {
        alert("Error on project delete: "+e)
        setShowLoadingScreen(false)
      })
    }else{
      deleteProjectById({project_id: id})
    }
  }
  const handleGoToStartPageClick = () => {
    if(allowServerComm){
      setShowLoadingScreen(true)
      axios.get(httpPaths["getProjects"], { email: userToken?.email })
      .then((response)=>{
        restoreProjects(response.data)
        setPageState(2)
        setShowLoadingScreen(false)
      }).catch(e => {
        alert("Error on project fetch: "+e)
        setPageState(2)
        setShowLoadingScreen(false)
      })  
    }else{
      setPageState(2)
    }
  }

  const handleProjectStart = (id) => {

    const nextUrl = getNavURL("/basic-info/inlet")

    if(allowServerComm){
      setShowLoadingScreen(true)
      axios.get(httpPaths["inOutletStream"], { project_id: id })
      .then((response)=>{
        setPageState(0)
        setShowLoadingScreen(false)
        reduxDispatch(update_selected_project_id(id))
        reduxDispatch(update_allowed_tabs({basicInfo: true}))
        reduxDispatch(update_bi_inlet_object(response.data[0]? response.data[0].u_inlet_strm: []))
        reduxDispatch(update_bi_outlet_object(response.data[1]? response.data[1].u_outlet_strm: []))
        navigate(nextUrl)
      }).catch(e => {
        alert("Error on start: "+e)
        setShowLoadingScreen(false)
      })
    }else{
      reduxDispatch(update_selected_project_id(id))
      reduxDispatch(update_allowed_tabs({basicInfo: true}))
      reduxDispatch(update_bi_inlet_object(BIInOuletSample[0].u_inlet_strm))
      reduxDispatch(update_bi_outlet_object(BIInOuletSample[1].u_outlet_strm))
      navigate(nextUrl)
    }
  }

  const renderedProjects = Projects.map((project)=>{
    return(
      <ProjectCard
        key={project.project_id}
        title={project.project_name} 
        description={project.project_description} 
        created={project.createtime}
        id={project.project_id}
        onStart={handleProjectStart}
        onDelete={handleProjectDelete}
        onEdit={handleProjectEdit}
      />
    )
  })

  const renderedFileOptions = pathFileList.map((file) => {
    return <option key={file} value={file}>{extractFileName(file)}</option>
  })

  if(!showLoadingScreen){
    if(pageState === 0){
      return(
        <div className="container content has-text-centered">
          <div className="block"/>
          <div className="features">
            <div className="columns is-mobile is-centered has-text-centered">
              <div className="column is-half">
                <div className="box">
                  <VscFileSubmodule className="icon has-text-primary is-large mb-3"/>
                  <button className="button is-primary" onClick={() => setPageState(1)}>New Project</button>
                </div>
              </div>

              <div className="column is-half">
                <div className="box">
                  <AiOutlineLineChart className="icon has-text-info is-large mb-3"/>
                  <button className="button is-info" onClick={handleGoToStartPageClick}>Start</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }else{
      return(
        <div>
          <div className="block"/>

          <div className="columns is-centered">
            <div className="column is-three-quarters">
              <div onClick={onBackPress}>
                <button className="button mb-5">Back</button>
              </div>

              {pageState === 1&&(
                <form className="box" onSubmit={handleNewProjectSave}>
                  <div className="field">
                    <label className="label">Title <span className="has-text-danger">*</span></label>
                    <div className="control">
                      <input className="input" type="text" placeholder="Project Title" value={projectTitle} onChange={handleProjectTitleInput}/>
                    </div>
                  </div>
                  <div className="field">
                    <label className="label">Description</label>
                    <div className="control">
                      <input className="input" type="text" placeholder="Project Description" value={projectDescription} onChange={handleProjectDescriptionInput}/>
                    </div>
                  </div>
                  <div className="file is-justify-content-space-between mt-5">
                    <div className="is-flex">
                      <label className="file-label">
                        <input className="file-input" type="file" name="fileList" webkitdirectory="" directory="" onChange={handleProjectFileSubmit}/>
                        <span className="file-cta">
                          <span className="file-icon">
                            <i className="fas fa-upload"></i>
                          </span>
                          <span className="file-label">
                            Upload directory <span className="has-text-danger">&nbsp;*</span>
                          </span>
                        </span>
                      </label>
                      {pathFileList.length>0&&(
                        <div className="select ml-5">
                          <select onChange={handleOptionSelect}>
                            {renderedFileOptions}
                          </select>
                        </div>
                      )}
                    </div>
                    <button className="button is-primary">Save</button>
                  </div>
                </form>
              )}
              {pageState === 2&&(
                <>
                  { Projects.length>0? (
                    <div className="list has-overflow-ellipsis has-visible-pointer-controls">
                      {renderedProjects}                  
                    </div>
                  ):(
                    <>
                      <div className="block"/>
                      <div className="columns is-centered">
                        <div className="column is-three-quarters has-text-centered">
                          <h1 className="title">No Projects Found</h1>
                          <p className="subtitle">Create a new project to start</p>
                        </div>
                      </div>
                    </>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      )
    }
  }else{
    return(
      <div className="pageloader is-active has-background-white is-light"><span className="title">Loading...</span></div>
    );
  }
}

export default HomePage;
